import React, { Component} from 'react';
import axios from 'axios';
import {
  ProfileSkills,
  ProfileComponent,
  ProfileList,
  ProfileItem,
  SpanItem,
  SpanWeb,
  Skills,
  SkillsDesc,
  Bar,
  BarTitle,
  BarPercent,
  BarParent,
  BarDivSpan,
  PSTitle,
  PSTitleSpan
} from './style.js';

class Profile extends Component {
  state = {
    infoProfile : [],
    skills : []
  }
  getDataProfile = () => {
    return( 
        axios.get('data.json').then(response => {
          console.log(response.data.profile)
          this.setState({
            infoProfile : response.data.profile.info,
            skills : response.data.profile.skills
            
          })
        })
        
    );
  }
  componentDidMount  ()  {
    this.getDataProfile()
    
    console.log('')
    console.log(this.state)
    
  }

 

  getSkills = () => {
    // const inforPr = this.state.infoProfile;
    const skills = this.state.skills;
    skills.map(skill => {
          return (
                      <Bar key={skill.id}> 
                          <BarTitle>{skill.title}</BarTitle>
                          <BarPercent>skill.percent</BarPercent>
                          <BarParent>
                              <BarDivSpan percent = {skill.percent}></BarDivSpan>
                          </BarParent>
                      </Bar>
          );
    })
  }

render(){
    return (
      <div >
        <ProfileSkills>
              <div className="container">
                  <ProfileComponent>
                      <PSTitle><PSTitleSpan>My </PSTitleSpan>Profile</PSTitle>
                      <ProfileList>
                          <ProfileItem>
                              <SpanItem>Name</SpanItem>
                              Hamza Nabil
                          </ProfileItem>
                          
                          <ProfileItem>
                              <SpanItem>Website</SpanItem>
                              <SpanWeb>www.google.com</SpanWeb>
                          </ProfileItem>
                      </ProfileList>
                  </ProfileComponent>
                  
                  <Skills>
                      <PSTitle>Some <PSTitleSpan>skills</PSTitleSpan></PSTitle>
                      <SkillsDesc>
                      Lorem ipsum dolor sit amet, consectetur adipisicing elit. Quos praesentium blanditiis esse cupiditate, omnis similique.
                      </SkillsDesc>
                      {this.getSkills()}
                  </Skills>
                  
              </div>
          </ProfileSkills>
      </div>
    );
  }
}

export default Profile;
