import styled from "styled-components";
import { Link } from "react-router-dom";

// ===============================
//         Navbar Styles
// ===============================

export const NavbarSection = styled.div`
  padding: 5px 0;
  overflow: hidden;
  background: #fff;
  position: relative;
  border-bottom: 1px solid #000;
`;

export const Logo = styled.div`
  width: 50%;
  float: left;
  @media {
    .navbar .logo {
      width: 100%;
      float: none;
    }
  }
`;

export const LogoText = styled.h2`
  font-size: 30px;
  font-weight: bold;
`;

export const UlList = styled.ul`
  width: 50%;
  float: left;
  list-style: none;
  padding: 0;
  @media {
    .navbar .ul-list {
      width: 100%;
      float: none;
      margin-top: 20px;
      display: none;
    }

    
  }
`;

export const ListItem = styled.li`
  display: inline-block;
`;

export const Anchor = styled.a`
  display: block;
  color: #222;
  text-decoration: none;
  padding: 10px 15px;
  font-weight: bold;
  &:hover {
    color: #eb5424;
  }
`;
export const LinkNew = styled(Link)`
  display: block;
  color: #222;
  text-decoration: none;
  padding: 10px 15px;
  font-weight: bold;
  &:hover {
    color: #eb5424;
  }
`;
